mapper.select:
/* retrieveCourseList */ SELECT COURSE.COURSE_CODE , COURSE.COURSE_NAME
, (CASE WHEN (SELECT COURSE_CODE FROM TB_STUDENT_REG WHERE COURSE_CODE IN(COURSE.COURSE_CODE) AND USER_CODE = #{userCode}) IS NOT NULL THEN 'N' ELSE 'Y' END) AS REGISTER_YN
, COURSE.COURSE_SCORE , COURSE_CATEGORY_CD , COURSE_ASSIGN_YN , COURSE_MAX_NUMBER , COURSE_CUR_NUMBER , (SELECT COMM_CD_NM FROM TB_COM_CODE WHERE 1=1 AND COMM_TY_CD = 'CODE0102' AND COMM_CD = COURSE.COURSE_CATEGORY_CD) AS COURSE_CATEGORY_CD_NM , COURSE_CLASSROOM , COURSE_DAY , COURSE_PERIOD , COURSE.COURSE_REG_DATE FROM TB_COURSE COURSE WHERE 1=1 ORDER BY COURSE.COURSE_REG_DATE DESC

mapper.insert:

/*	createCourse	*/
INSERT INTO TB_COURSE(
	  COURSE_CODE
	, COURSE_NAME
	, COURSE_SCORE
	, COURSE_MAX_NUMBER
    , COURSE_CLASSROOM
    , COURSE_DAY
    , COURSE_PERIOD
	, COURSE_REG_DATE
) VALUES (
	  'C' || LPAD(COURSE_SEQ.NEXTVAL, 4, 0)
	, #{courseName}
	, #{courseScore}
	, #{courseMaxNumber}
	, #{courseClassroom}
	, #{courseDay}
	, #{coursePeriod}
	, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
)

mapper.update:

/*	updateCurNumberPlus	*/
UPDATE TB_COURSE
SET COURSE_CUR_NUMBER = COURSE_CUR_NUMBER + 1
WHERE COURSE_CODE = #{courseCode}

mapper.delete:

/*	deleteCourse	*/
DELETE
FROM TB_COURSE
WHERE COURSE_CODE = #{courseCode}

mapper.update:

/*	updateCurNumberMinus	*/
UPDATE TB_COURSE
SET COURSE_CUR_NUMBER = COURSE_CUR_NUMBER - 1
WHERE COURSE_CODE = #{courseCode}


/*	updateAssignYn	*/
UPDATE TB_COURSE
SET COURSE_ASSIGN_YN = #{courseAssignYn}
WHERE COURSE_CODE = #{courseCode}


	/*	updateCourse	*/
	UPDATE TB_COURSE
	SET COURSE_NAME              = #{courseName}
	  , COURSE_SCORE	         = #{courseScore}
      , COURSE_CLASSROOM         = #{courseClassroom}
      , COURSE_DAY               = #{courseDay}
      , COURSE_PERIOD            = #{coursePeriod}
	WHERE COURSE_CODE = #{courseCode}
