mapper.insert:

INSERT INTO TB_PROFESSOR_REG(
  PROFESSOR_REG_CODE
, COURSE_CODE
, USER_CODE
) VALUES (
  'S' || LPAD(PROFESSOR_REG_SEQ.NEXTVAL, 4, 0)
, #{courseCode}
, #{userCode}
)

mapper.select:
SELECT PROFESSOR.PROFESSOR_REG_CODE , PROFESSOR.COURSE_CODE , COURSE.COURSE_NAME , COURSE.COURSE_SCORE , COURSE.COURSE_CATEGORY_CD , (SELECT COMM_CD_NM FROM TB_COM_CODE WHERE 1=1 AND COMM_TY_CD = 'CODE0102' AND COMM_CD = COURSE.COURSE_CATEGORY_CD) AS COURSE_CATEGORY_CD_NM , STUDENT.SCORE , STUDENT.STUDENT_REG_CODE , (SELECT NAME FROM TB_COM_USER CU WHERE CU.USER_CODE = STUDENT.USER_CODE) STUDENT_NAME , (SELECT NAME FROM TB_COM_USER CU WHERE CU.USER_CODE = PROFESSOR.USER_CODE) PROFESSOR_NAME FROM TB_PROFESSOR_REG PROFESSOR INNER JOIN TB_COURSE COURSE ON COURSE.COURSE_CODE = PROFESSOR.COURSE_CODE INNER JOIN TB_STUDENT_REG STUDENT ON STUDENT.COURSE_CODE = PROFESSOR.COURSE_CODE WHERE 1=1
AND PROFESSOR.USER_CODE = #{userCode}
AND STUDENT.USER_CODE = #{userCode}
